function formatRelationshipType(type: RelationshipType, gender?: Gender): string {
  const formats = {
    PARENT_IN_LAW: gender === "MALE" ? "Father-in-law" : 
                   gender === "FEMALE" ? "Mother-in-law" : "Parent-in-law",
    CHILD_IN_LAW: gender === "MALE" ? "Son-in-law" : 
                  gender === "FEMALE" ? "Daughter-in-law" : "Child-in-law",
    SIBLING_IN_LAW: gender === "MALE" ? "Brother-in-law" : 
                    gender === "FEMALE" ? "Sister-in-law" : "Sibling-in-law",
    STEP_PARENT: gender === "MALE" ? "Step-father" : 
                 gender === "FEMALE" ? "Step-mother" : "Step-parent",
    STEP_CHILD: gender === "MALE" ? "Step-son" : 
                gender === "FEMALE" ? "Step-daughter" : "Step-child",
    STEP_SIBLING: gender === "MALE" ? "Step-brother" : 
                  gender === "FEMALE" ? "Step-sister" : "Step-sibling",
  };

  return formats[type] || type.toLowerCase().replace(/_/g, ' ');
}
