### RSS Feed Implementation
```typescript
// apps/web/src/routes/rss.xml.ts
import RSS from "rss";

export async function GET() {
  const feed = new RSS({
    title: "Vamsa Family Updates",
    description: "Latest updates from your family tree",
    feed_url: `${process.env.APP_URL}/rss.xml`,
    site_url: process.env.APP_URL,
    image_url: `${process.env.APP_URL}/logo.png`,
    language: "en",
    pubDate: new Date(),
    ttl: 60  // 1 hour
  });

  // Get recent audit logs for feed items
  const recentUpdates = await prisma.auditLog.findMany({
    where: {
      action: { in: ["CREATE", "UPDATE"] },
      entityType: { in: ["Person", "Event", "MediaObject"] }
    },
    include: { user: true },
    orderBy: { createdAt: "desc" },
    take: 50
  });

  for (const update of recentUpdates) {
    let title = "";
    let description = "";
    let url = "";

    if (update.entityType === "Person") {
      const person = update.newData as Person;
      title = `${update.action === "CREATE" ? "New person added" : "Person updated"}: ${person.firstName} ${person.lastName}`;
      description = person.bio || `${person.firstName} ${person.lastName} was ${update.action === "CREATE" ? "added to" : "updated in"} the family tree`;
      url = `${process.env.APP_URL}/people/${update.entityId}`;
    } else if (update.entityType === "Event") {
      const event = update.newData as Event;
      title = `New event: ${event.type}`;
      description = event.description || `${event.type} event on ${event.date}`;
      url = `${process.env.APP_URL}/events/${update.entityId}`;
    }

    feed.item({
      title,
      description,
      url,
      date: update.createdAt,
      author: update.user.name
    });
  }

  return new Response(feed.xml(), {
    headers: {
      "Content-Type": "application/rss+xml; charset=utf-8",
      "Cache-Control": "public, max-age=3600"  // Cache for 1 hour
    }
  });
}
