// apps/web/src/routes/calendar/birthdays.ics.ts
import ical, { ICalCalendar } from "ical-generator";

export async function GET() {
  const calendar = ical({
    name: "Family Birthdays",
    description: "Birthdays of all family members",
    timezone: "America/New_York"
  });

  // Get all people with birthdays
  const people = await prisma.person.findMany({
    where: { dateOfBirth: { not: null } },
    select: {
      id: true,
      firstName: true,
      lastName: true,
      dateOfBirth: true,
      isLiving: true
    }
  });

  for (const person of people) {
    const birthMonth = person.dateOfBirth.getMonth();
    const birthDay = person.dateOfBirth.getDate();

    // Calculate age
    const birthYear = person.dateOfBirth.getFullYear();
    const currentYear = new Date().getFullYear();
    const age = currentYear - birthYear;

    // Create recurring yearly event
    calendar.createEvent({
      id: `birthday-${person.id}`,
      summary: `${person.firstName} ${person.lastName}${person.isLiving ? `s Birthday (${age})` : "s Birthday (Deceased)"}`,
      start: new Date(currentYear, birthMonth, birthDay),
      allDay: true,
      repeating: {
        freq: "YEARLY"
      },
      url: `${process.env.APP_URL}/people/${person.id}`,
      organizer: {
        name: "Vamsa Family Tree",
        email: "noreply@vamsa.app"
      },
      categories: [{ name: "Birthday" }]
    });
  }

  return new Response(calendar.toString(), {
    headers: {
      "Content-Type": "text/calendar; charset=utf-8",
