  confidence Float  // 0-1

  person1 DNATest @relation("Person1Matches", fields: [person1Id], references: [id])
  person2 DNATest @relation("Person2Matches", fields: [person2Id], references: [id])

  @@unique([person1Id, person2Id])
  @@index([person1Id])
  @@index([person2Id])
}
