```typescript
// Migrate existing single tree to multi-tree
async function migrateToMultiTree() {
  const settings = await prisma.familySettings.findFirst();

  // Create default tree
  const defaultTree = await prisma.tree.create({
    data: {
      name: settings.familyName,
      slug: "main",
      ownerId: adminUser.id,
      isPublic: false
    }
  });

  // Link all existing persons to default tree
  await prisma.person.updateMany({
    data: { treeId: defaultTree.id }
  });

  // Create tree memberships for all users
  const users = await prisma.user.findMany();
  await prisma.treeMember.createMany({
    data: users.map(user => ({
      treeId: defaultTree.id,
      userId: user.id,
      role: user.role === "ADMIN" ? "OWNER" : "VIEWER"
    }))
  });
}
