## Redis Client Wrapper
```typescript
// packages/lib/src/cache/redis.ts
import { createClient, RedisClientType } from "redis";

let client: RedisClientType;

export async function getRedisClient() {
  if (\!client) {
    client = createClient({ 
      url: process.env.REDIS_URL || "redis://localhost:6379"
    });
    await client.connect();
  }
  return client;
}

export async function cacheGet<T>(key: string): Promise<T | null> {
  const redis = await getRedisClient();
  const value = await redis.get(key);
  return value ? JSON.parse(value) : null;
}

export async function cacheSet(key: string, value: any, ttl: number) {
  const redis = await getRedisClient();
  await redis.setEx(key, ttl, JSON.stringify(value));
}

export async function cacheInvalidate(pattern: string) {
  const redis = await getRedisClient();
  const keys = await redis.keys(pattern);
  if (keys.length > 0) {
    await redis.del(keys);
  }
}
```

## Cache Invalidation Strategy
```typescript
// Invalidate person cache when person updated
await prisma.person.update({ ... });
await cacheInvalidate("person:*");
await cacheInvalidate("dashboard:*");
```

